amework.data.domain.Sort;import org.springframework.test.annotation.DirtiesContext;import org.springframework.test.context.junit4.SpringRunner;import java.time.Duration;import java.util.ArrayList;import java.util.List;import java.util.Optional;import java.util.UUID;import java.util.concurrent.ExecutionException;import static org.hamcrest.Matchers.*;import static org.junit.Assert.assertThat;import static org.junit.Assert.assertTrue;/** * @author Yoann Despréaux * @since 1.0.0 */@DirtiesContext@RunWith(SpringRunner.class)@SpringBootTest(classes = {        RestClientAutoConfiguration.class,        ElasticsearchTemplateConfiguration.class})public class ITArticleRepositoryTest {    @Autowired    private ArticleRepository repository;    @Autowired    private ElasticsearchOperations elasticsearchOperations;    private List<Article> articles;    @Before    public void setUp() {        this.articles = prepareData();    }    @Test    public void findById() {        Article articleIndexed = indexArticle("Article1", "Description de l'article", Article.EnumEntrepot.E1);        this.repository.refresh();        Optional<Article> optionalArticle = this.repository.findById(articleIndexed.getDocumentId());        assertTrue(optionalArticle.isPresent());        Article articleLoaded = optionalArticle.get();        assertThat(articleLoaded.getDocumentId(), is(equalTo(articleIndexed.getDocumentId())));        assertThat(articleLoaded.getName(), is(equalTo(articleIndexed.getName())));        assertThat(articleLoaded.getDescription(), is(equalTo(articleIndexed.getDescription())));        assertThat(articleLoaded.getEntrepot(), is(equalTo(articleIndexed.getEntrepot())));        assertThat(articleLoaded.getDocumentVersion(), is(equalTo(1L)));    }    @Test    public void findByIdNotFound() {        Optional<Article> optionalArticle = this.repository.findById(UUID.randomUUID().toString());        assertThat(optionalArticle.isPresent(), is(false));    }    @Test    public void existsById() {        Article articleIndexed = indexArticle("Article1", "Description de l'article", Article.EnumEntrepot.E1);        this.repository.refresh();        assertThat(this.repository.existsById(articleIndexed.getDocumentId()), is(true));    }    @Test    public void notExistsById() {        Article articleIndexed = indexArticle("Article1", "Description de l'article", Article.EnumEntrepot.E1);        this.repository.refresh();        assertThat(this.repository.existsById(UUID.randomUUID().toString()), is(false));    }    @Test    public void save() {        Article article = createArticle("Article1", "Description de l'article", Article.EnumEntrepot.E1);        Article articleIndexed = this.repository.save(article);        this.repository.refresh();        assertThat(articleIndexed.getDocumentId(), is(notNullValue()));        assertThat(articleIndexed.getName(), is(equalTo(article.getName())));        assertThat(articleIndexed.getDescription(), is(equalTo(article.getDescription())));        assertThat(articleIndexed.getEntrepot(), is(equalTo(article.getEntrepot())));        assertThat(articleIndexed.getDocumentVersion(), is(equalTo(1L)));    }    @Test    public void save_bulk() {        List<Article> articles = new ArrayList<>();        articles.add(createArticle("Article1", "Description de l'article 1", Article.EnumEntrepot.E1));        articles.add(createArticle("Article2", "Description de l'article 2", Article.EnumEntrepot.E2));        articles.add(createArticle("Article3", "Description de l'article 3", Article.EnumEntrepot.E1));        articles.add(createArticle("Article4", "Description de l'article 4", Article.EnumEntrepot.E3));        articles.add(createArticle("Article5", "Description de l'article 5", Article.EnumEntrepot.E1));        List<Article> articlesIndexed = this.repository.save(articles);        assertThat(articlesIndexed.size(), is(equalTo(articles.size())));        for (Article articleIndexed : articlesIndexed) {            assertThat(articleIndexed.getDocumentId(), is(notNullValue()));            assertThat(articleIndexed.getDocumentVersion(), is(equalTo(1L)));        }    }    @Test    public void deleteById() throws ExecutionException {        Article articleIndexed = indexArticle("Article1", "Description de l'article", Article.EnumEntrepot.E1);        this.repository.refresh();        this.repository.deleteById(articleIndexed.getDocumentId());        this.repository.refresh();        Optional<Article> optionalArticle = this.repository.findById(articleIndexed.getDocumentId());        assertThat(optionalArticle.isPresent(), is(false));    }    @Test    public void delete() throws ExecutionException {        Article articleIndexed = indexArticle("Article1", "Description de l'article", Article.EnumEntrepot.E1);        this.repository.refresh();        this.repository.delete(articleIndexed);        this.repository.refresh();        Optional<Article> optionalArticle = this.repository.findById(articleIndexed.getDocumentId());        assertThat(optionalArticle.isPresent(), is(false));    }    @Test    public void deleteAll() throws ExecutionException {        this.repository.refresh();        this.repository.deleteAll(articles);        this.repository.refresh();        for (Article articleIndexed : articles) {            Optional<Article> optionalArticle = this.repository.findById(articleIndexed.getDocumentId());            assertThat(optionalArticle.isPresent(), is(false));        }    }    @Test    public void searchByEntrepot_withoutSort() {        List<Article> result = this.repository.findByQuery(QueryBuilders.matchQuery("entrepot", "E1"), (Sort) null);        assertThat(result.size(), is(equalTo(3)));        for (Article article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }    }    @Test    public void searchByEntrepot_withSort() {        List<Article> result = this.repository.findByQuery(QueryBuilders.matchQuery("entrepot", "E1"), Sort.by(Sort.Direction.DESC, "name"));        assertThat(result.size(), is(equalTo(3)));        for (Article article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }        assertThat(result.get(0).getName(), is(equalTo("Article5")));        assertThat(result.get(1).getName(), is(equalTo("Article4")));        assertThat(result.get(2).getName(), is(equalTo("Article1")));    }    @Test    public void searchArticleInfoByEntrepot_withSort() {        List<ArticleInfo> result = this.repository.findByQuery(QueryBuilders.matchQuery("entrepot", "E1"), Sort.by(Sort.Direction.DESC, "name"), ArticleInfo.class);        assertThat(result.size(), is(equalTo(3)));        for (ArticleInfo article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));        }        assertThat(result.get(0).getName(), is(equalTo("Article5")));        assertThat(result.get(1).getName(), is(equalTo("Article4")));        assertThat(result.get(2).getName(), is(equalTo("Article1")));    }    @Test    public void searchByDescription_withSort() {        List<Article> result = this.repository.findByQuery(QueryBuilders.matchPhraseQuery("description", "Description"), Sort.by(Sort.Direction.ASC, "name"));        assertThat(result.size(), is(equalTo(5)));        for (Article article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }        assertThat(result.get(0).getName(), is(equalTo("Article1")));        assertThat(result.get(1).getName(), is(equalTo("Article2")));        assertThat(result.get(2).getName(), is(equalTo("Article3")));        assertThat(result.get(3).getName(), is(equalTo("Article4")));        assertThat(result.get(4).getName(), is(equalTo("Article5")));    }    @Test    public void searchByDescription_withScroll() {        Pageable pageable = PageRequest.of(0, 2, Sort.by(Sort.Direction.ASC, "name"));        Duration scrollTime = Duration.ofMinutes(1);        ScrolledPage<Article> result = this.repository.findByQuery(QueryBuilders.matchAllQuery(), pageable);        String scrollId = result.getScrollId();        assertThat(scrollId, is(notNullValue()));        assertThat(result.getTotalElements(), is(equalTo(5L)));        assertThat(result.getContent().size(), is(equalTo(2)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article1")));        assertThat(result.getContent().get(1).getName(), is(equalTo("Article2")));        result = this.repository.continueScroll(scrollId, scrollTime);        assertThat(result.getTotalElements(), is(equalTo(5L)));        assertThat(result.getContent().size(), is(equalTo(2)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article3")));        assertThat(result.getContent().get(1).getName(), is(equalTo("Article4")));        result = this.repository.continueScroll(scrollId, scrollTime);        assertThat(result.getTotalElements(), is(equalTo(5L)));        assertThat(result.getContent().size(), is(equalTo(1)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article5")));        result = this.repository.continueScroll(scrollId, scrollTime);        assertThat(result.getTotalElements(), is(equalTo(5L)));        assertThat(result.getContent().size(), is(equalTo(0)));    }    @Test    public void searchByAllFields_1_withSort() {        List<Article> result = this.repository.findByQuery(QueryBuilders.matchPhraseQuery("search_fields", "City"), Sort.by(Sort.Direction.ASC, "name"));        assertThat(result.size(), is(equalTo(1)));        for (Article article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }        assertThat(result.get(0).getName(), is(equalTo("Article1")));    }    @Test    public void searchByAllFields_2_withSort() {        List<Article> result = this.repository.findByQuery(QueryBuilders.matchPhraseQuery("search_fields", "E2"), Sort.by(Sort.Direction.ASC, "name"));        assertThat(result.size(), is(equalTo(1)));        for (Article article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }        assertThat(result.get(0).getName(), is(equalTo("Article2")));    }    @Test    public void searchArticleInfoByDescription_withScroll() {        Pageable pageable = PageRequest.of(0, 2, Sort.by(Sort.Direction.ASC, "name"));        ScrolledPage<ArticleInfo> result = this.repository.findByQuery(QueryBuilders.matchAllQuery(), pageable, ArticleInfo.class);        assertThat(result.getScrollId(), is(notNullValue()));        assertThat(result.getTotalElements(), is(equalTo(5L)));        assertThat(result.getContent().size(), is(equalTo(2)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article1")));        assertThat(result.getContent().get(1).getName(), is(equalTo("Article2")));        result = this.repository.continueScroll(result.getScrollId(), Duration.ofMinutes(1), ArticleInfo.class);        assertThat(result.getTotalElements(), is(equalTo(5L)));        assertThat(result.getContent().size(), is(equalTo(2)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article3")));        assertThat(result.getContent().get(1).getName(), is(equalTo("Article4")));    }    @Test    public void findArticleByEntrepotOrderByNameDesc() {        List<Article> result = this.repository.findArticleByEntrepotOrderByNameDesc(Article.EnumEntrepot.E1);        assertThat(result.size(), is(equalTo(3)));        for (Article article : result) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }        assertThat(result.get(0).getName(), is(equalTo("Article5")));        assertThat(result.get(1).getName(), is(equalTo("Article4")));        assertThat(result.get(2).getName(), is(equalTo("Article1")));    }    @Test    public void findArticleByEntrepot() {        Pageable pageable = PageRequest.of(0, 2, Sort.by(Sort.Direction.ASC, "name"));        ScrolledPage<Article> result = this.repository.findArticleByEntrepot(Article.EnumEntrepot.E1, pageable);        assertThat(result.getTotalElements(), is(equalTo(3L)));        for (Article article : result.getContent()) {            assertThat(article.getDocumentId(), is(notNullValue()));            assertThat(article.getDocumentVersion(), is(notNullValue()));        }        assertThat(result.getContent().size(), is(equalTo(2)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article1")));        assertThat(result.getContent().get(1).getName(), is(equalTo("Article4")));        result = this.repository.continueScroll(result.getScrollId(), Duration.ofMinutes(1));        assertThat(result.getTotalElements(), is(equalTo(3L)));        assertThat(result.getContent().size(), is(equalTo(1)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article5")));    }    @Test    public void count() {        assertThat(this.repository.count(), is(equalTo(5L)));    }    @Test    public void countByCriteria() {        assertThat(this.repository.count(Criteria.where("entrepot").is(Article.EnumEntrepot.E1)), is(equalTo(3L)));    }    @Test    public void countByQuery() {        assertThat(this.repository.count(QueryBuilders.matchAllQuery()), is(equalTo(5L)));    }    @Test    public void countByEntrepot() {        assertThat(this.repository.countArticleByEntrepot(Article.EnumEntrepot.E1), is(equalTo(3L)));    }    @Test    public void countByDescription() {        assertThat(this.repository.countByDescriptionContains("3"), is(equalTo(1L)));    }    @Test    public void searchWithOrCriteria() {        List<Article> result = this.repository.findByQuery(Criteria.where("name").is("Article1").or("name").is("Article2"), Sort.by(Sort.Direction.ASC, "name"));        assertThat(result.size(), is(equalTo(2)));        assertThat(result.get(0).getName(), is(equalTo("Article1")));        assertThat(result.get(1).getName(), is(equalTo("Article2")));    }    @Test    public void searchWithNegativeCriteria() {        List<Article> result = this.repository.findByQuery(Criteria.where("name").is("Article1").not(), Sort.by(Sort.Direction.ASC, "name"));        assertThat(result.size(), is(equalTo(4)));        assertThat(result.get(0).getName(), is(equalTo("Article2")));        assertThat(result.get(1).getName(), is(equalTo("Article3")));        assertThat(result.get(2).getName(), is(equalTo("Article4")));        assertThat(result.get(3).getName(), is(equalTo("Article5")));    }    @Test    public void searchWithCriteriaByNameAndEntrepot() {        List<Article> result = this.repository.findByQuery(Criteria.where("name").is("Article1").and("entrepot").is(Article.EnumEntrepot.E1.name()), Sort.by(Sort.Direction.ASC, "name"));        assertThat(result.size(), is(equalTo(1)));        assertThat(result.get(0).getName(), is(equalTo("Article1")));    }    @Test    public void searchWithCriteriaByNameAndEntrepotWithPageable() {        ScrolledPage<Article> result = this.repository.findByQuery(Criteria.where("name").is("Article1").and("entrepot").is(Article.EnumEntrepot.E1.name()),                PageRequest.of(0, 10, Sort.by(Sort.Direction.ASC, "name")));        assertThat(result.getTotalElements(), is(equalTo(1L)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article1")));    }    @Test    public void searchWithCriteriaByNameAndEntrepotWithPageableAndProjection() {        ScrolledPage<ArticleInfo> result = this.repository.findByQuery(Criteria.where("name").is("Article1").and("entrepot").is(Article.EnumEntrepot.E1.name()),                PageRequest.of(0, 10, Sort.by(Sort.Direction.ASC, "name")),                ArticleInfo.class);        assertThat(result.getTotalElements(), is(equalTo(1L)));        assertThat(result.getContent().get(0).getName(), is(equalTo("Article1")));        this.repository.clearSearch(result.getScrollId());    }    @Test    public void findByEntrepot() {        List<Article> result = this.repository.findByEntrepot(Article.EnumEntrepot.E1);        assertThat(result.size(), is(equalTo(3)));    }    @Test    public void findByEntrepotOrderByNameAsc() {        List<Article> result = this.repository.findByEntrepotOrderByNameAsc(Article.EnumEntrepot.E1);        assertThat(result.size(), is(equalTo(3)));        assertThat(result.get(0).getName(), is(equalTo("Article1")));        assertThat(result.get(1).getName(), is(equalTo("Article4")));        assertThat(result.get(2).getName(), is(equalTo("Article5")));    }    @Test    public void findByName() {        Article result = this.repository.findByName("Article1");        assertThat(result.getName(), is(equalTo("Article1")));    }    @Test    public void findByNameQuery() {        Article result = this.repository.findByNameQuery("Article1");        assertThat(result.getName(), is(equalTo("Article1")));    }    @Test    public void findByEntrepotQuery() {        List<Article> result = this.repository.findByEntrepotQuery(Article.EnumEntrepot.E1);        assertThat(result.size(), is(equalTo(3)));    }    @Test    public void findByEntrepotQueryWithPageable() {        ScrolledPage<Article> result = this.repository.findByEntrepotQuery(Article.EnumEntrepot.E1, PageRequest.of(0, 5));        assertThat(result.getTotalElements(), is(equalTo(3L)));    }    private List<Article> prepareData() {        List<Article> articles = new ArrayList<>();        articles.add(createArticle("Article1", "Description de l'article 1 - City", Article.EnumEntrepot.E1));        articles.add(createArticle("Article2", "Description de l'article 2 - Product", Article.EnumEntrepot.E2));        articles.add(createArticle("Article3", "Description de l'article 3 - Company", Article.EnumEntrepot.E3));        articles.add(createArticle("Article4", "Description de l'article 4 - Step", Article.EnumEntrepot.E1));        articles.add(createArticle("Article5", "Description de l'article 5 - World", Article.EnumEntrepot.E1));        this.repository.deleteAll();        this.repository.save(articles);        this.repository.refresh();        return articles;    }    /**     * @param name     * @param description     * @param entrepot     * @return     */    private Article createArticle(String name, String description, Article.EnumEntrepot entrepot) {        return Article.builder()                .name(name)                .description(description)                .entrepot(entrepot)                .build();    }    private Article indexArticle(String name, String description, Article.EnumEntrepot entrepot) {        return this.elasticsearchOperations.index(createArticle(name, description, entrepot), Article.class);    }}